mapper.select:
select
count(`id`)
from `sys_menu`


		and `id` = #{id}


		and `pid` = #{pid}


		and `name` = #{name}


		and `is_menu` = #{isMenu}


		and `condition` = #{condition}


		and `front_path` = #{frontPath}


		and `router_method` = #{routerMethod}


		and `seq` = #{seq}


		and `icon` = #{icon}


		and `status` = #{status}


		and `i_frame` = #{iFrame}


		and `menu_type` = #{menuType}


		and `path` = #{path}


		and `permission` = #{permission}


		and `cache` = #{cache}


		and `hidden` = #{hidden}


		and `component` = #{component}


		and `component_name` = #{componentName}


		and `create_time` = #{createTime}

		select

		from `sys_menu`


>				and `id` = #{record.id}


